    private void type_of_election_comboboxActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_type_of_election_comboboxActionPerformed
        int t=type_of_election_combobox.getSelectedIndex();
        if (t==1) {
            countycombobox_election.setEnabled(true);
            constituencycombobox_election.setEnabled(false);
            ward_combo_election.setEnabled(false);
        } else if(t==2){
            constituencycombobox_election.setEnabled(true);
            countycombobox_election.setEnabled(false);
            ward_combo_election.setEnabled(false);
        }else if(t==3){
            ward_combo_election.setEnabled(true);
            constituencycombobox_election.setEnabled(false);
            countycombobox_election.setEnabled(false);
        }else{
            countycombobox_election.setEnabled(false);
            constituencycombobox_election.setEnabled(false);
            ward_combo_election.setEnabled(false);
        }
}//GEN-LAST:event_type_of_election_comboboxActionPerformed

