    private void add_new_election1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_add_new_election1ActionPerformed
        
        try {
            String ele=election_id.getText();
            String type=type_of_election_combobox.getSelectedItem().toString();
            String ele_date=Election_date.getText();
            String cou=countycombobox_election.getSelectedItem().toString();
            String con=constituencycombobox_election.getSelectedItem().toString();
            String war=ward_combo_election.getSelectedItem().toString();
            if (cou == null || con==null|| war==null) {
                cou.equals("N/A");
                con.equals("N/A");
                war.equals("N/A");
            }
            
            
            try{
                String sql="INSERT INTO election_details VALUES("+
                        "'"+ele+"'," +
                        "'"+type+"',"+
                        "'"+ele_date+"',"+
                        "'"+cou+"',"+
                        "'"+con+"',"+
                        "'"+war+"')";
                
                st.execute(sql);
                JOptionPane.showMessageDialog(null,"The following has been saved successfully\n Election ID: "+ele+"\nElection Type: "+type+"\nElection Date: "+ele_date+"\nCounty: "+cou+"\nConstituency: "+con+"\nWard: "+war+"\n","Information",JOptionPane.INFORMATION_MESSAGE );
                
                
                
                
                
                
                
                
            }catch(Exception exp){
                String s=exp.getMessage();
                JOptionPane.showMessageDialog(null,"Please select the election date. ","Error",JOptionPane.ERROR_MESSAGE);
            }
            
            
            
            
            
            
            
            
            
        } catch (Exception e) {
            
            JOptionPane.showMessageDialog(null, e.getMessage(),"Error",JOptionPane.ERROR_MESSAGE);
        }
        
        
        
        
        // TODO add your handling code here:
}//GEN-LAST:event_add_new_election1ActionPerformed

