    private void ward_combo_verificationActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ward_combo_verificationActionPerformed
        // TODO add your handling code here:
        String selected_ward=ward_combo_verification.getSelectedItem().toString();
        try {
                    
                    String sql="Select * from ward_database where name ='"+selected_ward+"'";
                    rs=st.executeQuery(sql);
                    st=conn.prepareStatement(sql);
                    while (rs.next()) { 
                    String constituency=rs.getString("county");
                    String county=rs.getString("constituency");
                    v_con.setText(constituency);
                    v_cou.setText(county);
                
            }
                    
        } catch (Exception e) {
            JOptionPane.showMessageDialog(electionpane, e);
        }
}//GEN-LAST:event_ward_combo_verificationActionPerformed

