    private void verification_buttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_verification_buttonActionPerformed
        String temp = verification_id_number.getText() ;
        String ver=verification_voter_id.getText();
        String sql = "Select * from registration where id_no ='"+temp+"' and voter_id ='"+ver+"'";
        
        try{
            int c=0;
            rs = st.executeQuery(sql);
            st = conn.prepareStatement(sql);
            
            while(rs.next()){
                c++;
            }
            if (c==1) {
                
                
                rs = st.executeQuery(sql);
                st = conn.prepareStatement(sql);
                verification_table.setModel(DbUtils.resultSetToTableModel(rs));
            } else {
                JOptionPane.showMessageDialog(null, "ID Number: "+temp+"\nVoter ID : "+ver+"\n is not registered" );
            }
            
            
            
            
        } catch(Exception exp){
            JOptionPane.showMessageDialog(null, exp,"Error",JOptionPane.ERROR_MESSAGE);
            
            
        }
}//GEN-LAST:event_verification_buttonActionPerformed

