    private void savebuttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_savebuttonActionPerformed
        
        String sur=sur_name.getText();
        //        String mn=
        if (sur.equals("")|| Middle_name.getText().equals("")||last_name.getText().equals("")|| ward_combo.getSelectedIndex()==0) {
            JOptionPane.showMessageDialog(null, "Please fill in all your details!","INFO",JOptionPane.INFORMATION_MESSAGE);
            
        } else {
            
            String idno=id_card_no.getText();
            int l=idno.length();
            
            int m=month.getSelectedIndex()+1;
            int y=Integer.parseInt(year.getSelectedItem().toString()) ;
            int da=Integer.parseInt(date.getSelectedItem().toString()) ;
            
            //        get the current date
            DateFormat dateFormat = new SimpleDateFormat("yyyy");
            java.util.Date leo = new java.util.Date();
            int thisYear = Integer.parseInt(dateFormat.format(leo));
            
            dateFormat = new SimpleDateFormat("MM");
            leo = new java.util.Date();
            int thisMonth = Integer.parseInt(dateFormat.format(leo));
            
            dateFormat = new SimpleDateFormat("dd");
            leo = new java.util.Date();
            int thisDay = Integer.parseInt(dateFormat.format(leo));
            
            //CREATE AN AGE VARIABLE TO HOLD THE CALCULATED AGE
            //TO START WILL  SET THE AGE EQUEL TO THE CURRENT YEAR MINUS THE YEAR
            //OF THE DOB
            int age = thisYear - y;
            
            //IF THE CURRENT MONTH IS LESS THAN THE DOB MONTH
            //THEN REDUCE THE DOB BY 1 AS THEY HAVE NOT HAD THEIR
            //BIRTHDAY YET THIS YEAR
            if(thisMonth < m){
                age = age - 1;
            }
            
            //IF THE MONTH IN THE DOB IS EQUEL TO THE CURRENT MONTH
            //THEN CHECK THE DAY TO FIND OUT IF THEY HAVE HAD THEIR
            //BIRTHDAY YET. IF THE CURRENT DAY IS LESS THAN THE DAY OF THE DOB
            //THEN REDUCE THE DOB BY 1 AS THEY HAVE NOT HAD THEIR
            //BIRTHDAY YET THIS YEAR
            if(thisMonth == m && thisDay < m){
                age = age - 1;
            }
            
            //THE AGE VARIBALE WILL NOW CONTAIN THE CORRECT AGE
            //DERIVED FROMTHE GIVEN DOB
            //        System.out.println(age);
            
            //        String n=sur_name.getText();
            //        if(n.equals("")){
            //            sur_name.setBackground(Color.lightGray);
            //        }
            if(l<8 || l>9){
                id_card_no.setBackground(Color.lightGray);
            }else if (age>17 ) {
                try {
                    String num=id_card_no.getText();
                    String surname=sur_name.getText();
                    String middle=Middle_name.getText();
                    String last=last_name.getText();
                    String gen=buttonGroup1.getSelection().getActionCommand();
                    String DOB=y+"-"+da+"-"+m;
                    String voteid=voterid.getText();
                    String p=place_of_issue_combo.getSelectedItem().toString();
                    String w=ward_combo.getSelectedItem().toString();
                    String cons=constituency_textfield.getText();
                    String cou=county_textfield.getText();
                    String pl=polling_stations_combo.getSelectedItem().toString();
                    String date_of_birth=datefield.getText();
                    
                    
                    try{
                        String sql="INSERT INTO registration VALUES("+
                                "'"+num+"'," +
                                "'"+surname+"',"+
                                "'"+middle+"',"+
                                "'"+last+"',"+
                                "'"+gen+"',"+
                                "'"+DOB+"',"+
                                "'"+voteid+"',"+
                                "'"+p+"',"+
                                "'"+w+"',"+
                                "'"+cons+"',"+
                                "'"+cou+"',"+
                                "'"+pl+"',"+
                                "'"+date_of_birth+"')";
                        
                        st.execute(sql);
                        JOptionPane.showMessageDialog(null,"The following has been saved\n ID number: "+num+"\nSir Name: "+surname+"\nMiddle Name: "+middle+"\nLast Name: "+last+"\nGender: "+gen+"\nAge: "+DOB+"\nVoter ID: "+voteid+"\nPlace of Issue: "+p+"\nWard: "+w+"\nConstituency: "+cons+"\nCounty: "+cou+"\nPolling Station: "+pl+"\nDate of Issue: "+date_of_birth+"\n","Information",JOptionPane.INFORMATION_MESSAGE );                                                                                             
                        id_card_no.setText("");
                        sur_name.setText("");
                        Middle_name.setText("");
                        last_name.setText("");
                        //                  age.setText("");
                        place_of_issue_combo.setSelectedIndex(0);
                        ward_combo.setSelectedIndex(0);
                        polling_stations_combo.setSelectedIndex(0);
                        constituency_textfield.setText("");
                        county_textfield.setText("");
                        malebutton.setSelected(true);
                        voterid.setText(""+(int)(Math.random()*200));
                        year.setSelectedIndex(0);
                        month.setSelectedIndex(0);
                        date.setSelectedIndex(0);
                        
                        
                        
                        
                        
                        
                        
                        
                        
                        
                    }catch(SQLException exp){
                        
                        JOptionPane.showMessageDialog(null, exp.getMessage());
                    }
                    
                } catch (Exception e) {
                    
                    JOptionPane.showMessageDialog(null, e.getMessage());
                }
                
            } else {
                
                JOptionPane.showMessageDialog(null,"Sorry your age is below the expected minimum of 18 years.","ERROR",JOptionPane.ERROR_MESSAGE);
                
            }
        }
        
        
        
        
        
        
    }//GEN-LAST:event_savebuttonActionPerformed

