    private void constituency_search_button_ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_constituency_search_button_ActionPerformed

        
        String temp = constituency_search.getText() + "%";
        String sql = "Select * from constituency_database where name like'"+temp+"'";
        try{
            st = conn.prepareStatement(sql);
            rs = st.executeQuery(sql);
            constituency_table.setModel(DbUtils.resultSetToTableModel(rs));
            if(!rs.absolute(1)){ JOptionPane.showMessageDialog(null, "No results were found: " );}
                

        } catch(Exception exp){
            System.out.println(exp);
           

        }
        // TODO add your handling code here:
    }//GEN-LAST:event_constituency_search_button_ActionPerformed

