    private void cou_saveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cou_saveActionPerformed
String county_id=county_id_county.getText();
String cou_name_c=countycombobox_county.getSelectedItem().toString();
String sql = "Select name from county_database where name like'"+cou_name_c+"'";
        try{
            st = conn.prepareStatement(sql);
            rs = st.executeQuery(sql);
            int c=0;
                  while(rs.next()){
                    c++;
                    }
            if(c==0){ 
            try{
	            String sql2="INSERT INTO county_database VALUES("+
                              "'"+county_id+"'," +
                            
	                       "'"+cou_name_c+"')";

	              st.execute(sql2);
	                JOptionPane.showMessageDialog(rootPane,"The information has been saved :-\n"+county_id+",\n "+cou_name_c);


	          county_id_county.setText("COU"+(int)(Math.random()*47));
                  countycombobox_county.setSelectedIndex(0);

	          

	            }catch(Exception exp){
	              JOptionPane.showMessageDialog(null, exp.getMessage(),"ERROR",JOptionPane.ERROR_MESSAGE);
	            }
            
            
            
            }
            else
                JOptionPane.showMessageDialog(null, cou_name_c+"\n IS ALREADY SAVED IN THE DATABASE","DUPLICATE",JOptionPane.ERROR_MESSAGE);
                
                

        } catch(Exception exp){
            JOptionPane.showMessageDialog(null, exp,"ERROR",JOptionPane.ERROR_MESSAGE);
           

        }





        // TODO add your handling code here:
    }//GEN-LAST:event_cou_saveActionPerformed

