    private void con_saveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_con_saveActionPerformed
String con_id=constituency_id.getText();
String con_name_c=constituencycombobox_con.getSelectedItem().toString();
String cou_con=countycombobox_constituency.getSelectedItem().toString();

//ENSURE SELECTION OF CONSTITUENCY NAME IS VALID AND SAVING INTO THE DATABASE
String sql = "Select name from constituency_database where name like'"+con_name_c+"'";
        try {
            st = conn.prepareStatement(sql);
            rs = st.executeQuery(sql);
            int c=0;
                  while(rs.next()){
                    c++;
                    }
                  if (c==0) {
                      try{
	            String sql2="INSERT INTO constituency_database VALUES("+
                              "'"+con_id+"'," +
                            "'"+con_name_c+"'," + 
	                       "'"+cou_con+"')";

	              st.execute(sql2);
	                JOptionPane.showMessageDialog(null,"The information has been saved :\n"+con_id+",\n "+con_name_c+",\n "+cou_con);


                  constituency_id.setText("CON"+(int)(Math.random()*290));
                  countycombobox_county.setSelectedIndex(0);
                  constituencycombobox_con.setSelectedIndex(0);

	          

	            }catch(Exception exp){
	              JOptionPane.showMessageDialog(null, exp.getMessage(),"ERROR",JOptionPane.ERROR_MESSAGE);
	            }
                
            } else {
                      JOptionPane.showMessageDialog(null, con_name_c+"\n IS ALREADY SAVED IN THE DATABASE","DUPLICATE",JOptionPane.ERROR_MESSAGE);
     
            }
            
        } catch (Exception e) {
            
        }



    }//GEN-LAST:event_con_saveActionPerformed

