    private void war_saveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_war_saveActionPerformed

        
        // TODO add your handling code here:
String ward=ward_id.getText();
String ward_n=ward_combo.getSelectedItem().toString();
String ward_con=constituencycombobox_ward.getSelectedItem().toString();
String ward_cou=countycombobox_ward.getSelectedItem().toString();
String sql = "Select name from ward_database where name like'"+ward_n+"'";
        if (ward_n==null) {
            JOptionPane.showMessageDialog(rootPane, "WARD NAME CANNOT BE NULL","INFORMATION",JOptionPane.INFORMATION_MESSAGE);

        } else {
            try {
                st = conn.prepareStatement(sql);
                rs = st.executeQuery(sql);
                int c=0;
                  while(rs.next()){
                    c++;
                    }
                if (c==0) {
                    try{
	            String sql2="INSERT INTO ward_database VALUES("+
                              "'"+ward+"'," +
                            "'"+ward_n+"'," + 
                            "'"+ward_con+"'," +
	                       "'"+ward_cou+"')";

	              st.execute(sql2);
	                JOptionPane.showMessageDialog(null,"The information has been saved :\n"+ward+",\n "+ward_n+",\n "+ward_con+",\n "+ward_cou);


                  ward_id.setText("CON"+(int)(Math.random()*290));
                  ward_combo.setSelectedIndex(0);
                  constituencycombobox_ward.setSelectedIndex(0);
                  countycombobox_ward.setSelectedIndex(0);

	          

	            }catch(Exception exp){
	              JOptionPane.showMessageDialog(null, exp.getMessage(),"ERROR",JOptionPane.ERROR_MESSAGE);
	            }
                    
                } else {
                   JOptionPane.showMessageDialog(null, ward_n+"\n IS ALREADY SAVED IN THE DATABASE","DUPLICATE",JOptionPane.ERROR_MESSAGE); 
                }
                
            } catch (Exception e) {
            }
        }





    }//GEN-LAST:event_war_saveActionPerformed

