    private void county_search_button_ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_county_search_button_ActionPerformed

        
        String temp = county_search.getText() + "%";
        String sql = "Select * from county_database where name like'"+temp+"'";
        try{
            st = conn.prepareStatement(sql);
            rs = st.executeQuery(sql);
            county_table.setModel(DbUtils.resultSetToTableModel(rs));
            if(!rs.absolute(1)){ JOptionPane.showMessageDialog(null, "No results were found: " );}
                

        } catch(Exception exp){
            System.out.println(exp);
           

        }
        // TODO add your handling code here:
    }//GEN-LAST:event_county_search_button_ActionPerformed

